txt = canvas.create_text(
    250.0,
    250.0,
    anchor="nw",
    text=data.get(),
    fill="#FFFFFF",
    font=("Roboto", 30 * -1)
)

def on_change(varname, index, mode):
    canvas.itemconfigure(txt, text=window.getvar(varname))

data.trace_variable('w', on_change)

def trigger_change(val):
    data.set(val)

def read_sensor():
    
    while True:
        data_temp = ""
        for i in range(0, 15):
            data_temp += chr(bus.read_byte(address));
        print(data_temp[:2])
        trigger_change(data_temp[:2])
        time.sleep(0.1)

# create the thread
task = threading.Thread(target=read_sensor, daemon=True)

task.start()
window.resizable(False, False)
window.mainloop()
